script:
frappe.ui.form.on('Work Order', {
	onload(frm) {
		// your code here
		cur_frm.add_fetch('BOM',  'collect_production_item',  'collect_production_item');
	}
})

frappe.ui.form.on('Stock Entry', {
	refresh(frm) {
		// your code here
		if (frm.doc.stock_entry_type ==='Manufacture') { 
		    frm.set_df_property("collect_production_item", "reqd", frm.doc.stock_entry_type=="Manufacture");
		    frm.add_fetch('work_order',  'collect_production_item',  'collect_production_item');
		}
		else
		{
		   frm.toggle_reqd("collect_production_item", false);
		}
	}
})
